```typescript
import * as pulumi from "@pulumi/pulumi";
import * as opensearch from "@piclemx/pulumi-opensearch";

const test = new opensearch.AuditConfig("test", {
    audit: {
        disabledRestCategories: [
            "GRANTED_PRIVILEGES",
            "AUTHENTICATED",
        ],
        disabledTransportCategories: [
            "GRANTED_PRIVILEGES",
            "AUTHENTICATED",
        ],
        enableRest: true,
        enableTransport: true,
        excludeSensitiveHeaders: true,
        ignoreRequests: [
            "SearchRequest",
            "indices:data/read/*",
            "/_cluster/health",
        ],
        ignoreUsers: ["dashboardserver"],
        logRequestBody: true,
        resolveBulkRequests: true,
        resolveIndices: true,
    },
    compliance: {
        enabled: true,
        externalConfig: false,
        internalConfig: true,
        readIgnoreUsers: ["read-ignore-1"],
        readMetadataOnly: true,
        readWatchedFields: [
            {
                fields: [
                    "field-1",
                    "field-2",
                ],
                index: "read-index-1",
            },
            {
                fields: ["field-3"],
                index: "read-index-2",
            },
        ],
        writeIgnoreUsers: ["write-ignore-1"],
        writeLogDiffs: false,
        writeMetadataOnly: true,
        writeWatchedIndices: [
            "write-index-1",
            "write-index-2",
            "log-*",
            "*",
        ],
    },
    enabled: true,
});
